import requests 
from bs4 import BeautifulSoup as BS
def get_platformnum_from_ir(station_name):
	r = requests.get('http://indiarailinfo.com/shtml/list.shtml?LappGetStationList/' + station_name + '/0/0/0?')
	bs = BS(r.text, 'html.parser')
	table = bs.findAll('table')
	if len(table) > 0: 
		table = table[0]
		tr = table.findAll('tr')
		if len(tr) > 0:
			tr = tr[0]
			num = tr.findAll('td')[0].string
			r = requests.get('http://indiarailinfo.com/arrivals/' + num + '?')
			bs = BS(r.text, 'html.parser')
			div = bs.findAll('div', {'class':'topcapsule'})[0]
			h1 = div.find('h1')
			text = h1.text
			if 'PFs' in text :
				pfnum = int(text.split(' PFs')[0].split('(')[1])
				return {'data' : pfnum}
			else :
				return {'error' : 'no platform number in html'}

string = raw_input('enter name \n')
get_platformnum_from_ir(string)